import React, { Component } from 'react'
import './Counter.css'
import { render } from '@testing-library/react'
import PropTypes from 'prop-types'


class Counter extends Component {

        //constructor  - define the insitial state in constructor
    // set the state at start in constructor , counter = 0
    constructor(){
        super();
        this.state = { 
            counter : 0
        }
        this.increment = this.increment.bind(this)
    }

    render(){
        return (
            <div className="counter">
                <CounterButton by = {1} incrementMethod = {this.increment}></CounterButton>
                <CounterButton by = {5} incrementMethod = {this.increment}></CounterButton>
                <CounterButton by = {10} incrementMethod = {this.increment}></CounterButton>

            </div>
        );
    }

    increment(by){
         console.log(`increment from parent ${by}`)
       // this.setState( {
         //   counter : this.state.counter //+ this.props.by
        //});
     }
}

class CounterButton extends Component {

    //constructor  - define the insitial state in constructor
    // set the state at start in constructor , counter = 0
    constructor(){
        super();
        this.state = { 
            counter : 0
        }
        this.increment = this.increment.bind(this)
    }

    render(){
        return(
            <div className = "counterButton">
                <button onClick = {this.increment}>+{this.props.by}</button>
                <span>{this.state.counter}</span>
                <button className = "refreshBtn"> Refresh</button>
            </div>
        )
    }

    increment(){
       // console.log('increment')
       this.setState( {
           counter : this.state.counter + this.props.by
       });

       this.props.incrementMethod(this.props.by)
    }
}

CounterButton.defaultProps = {
    by : 1
}

CounterButton.propTypes = {
    by : PropTypes.number
}
export default Counter;
